                } else {
                    emitter.onError(new Exception("Failed to connect"));
                }
            } catch (Exception e) {
                emitter.onError(e);
            }
        });
    }

    private String getFileNameFromHeader(String header) {
        if (header != null && header.contains("filename=")) {
            return header.substring(header.indexOf("filename=") + 9).replace("\"", "");
        }
        return "downloaded_file";
    }
}
