            connection.setDoOutput(true);
            try (OutputStream os = connection.getOutputStream()) {
                if (xmlStr != null) {
                    os.write(xmlStr.getBytes(StandardCharsets.UTF_8));
                }
            }
        }

        int responseCode = connection.getResponseCode();
        InputStream inputStream;
        if (responseCode >= 200 && responseCode < 300) {
            inputStream = connection.getInputStream();
        } else {
            inputStream = connection.getErrorStream();
        }

        String xmlResponse = readStream(inputStream);
        Map<String, Object> resultMap = parseXmlToMap(xmlResponse);
        return resultMap;
    }

    private static String readStream(InputStream is) throws IOException {
        StringBuilder sb = new StringBuilder();
        BufferedReader reader = new BufferedReader(new InputStreamReader(is));
        String line;
        while ((line = reader.readLine()) != null) {
            sb.append(line);
        }
        return sb.toString();
    }

    private static Map<String, Object> parseXmlToMap(String xmlStr) throws Exception {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();
        InputSource is = new InputSource(new StringReader(xmlStr));
        Document doc = builder.parse(is);
        doc.getDocumentElement().normalize();

        Map<String, Object> map = new java.util.HashMap<>();
        processNode(doc.getDocumentElement(), map);
        return map;
    }

    private static void processNode(Node node, Map<String, Object> map) {
        if (node.getNodeType() == Node.ELEMENT_NODE) {
            Element element = (Element) node;
            String tagName = element.getTagName();
            String textContent = element.getTextContent();
            map.put(tagName, textContent);
            NodeList children = element.getChildNodes();
            for (int i = 0; i < children.getLength(); i++) {
                processNode(children.item(i), map);
            }
        }
    }
}

