import io.netty.handler.codec.http.multipart.*;
import io.netty.util.CharsetUtil;
import java.net.URI;
import java.util.*;
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicReference;

public class TestHttpClient {
    private final String host;
    private final int port;
    private final int defaultTimeout;
    private final NioEventLoopGroup workerGroup;
    private final Bootstrap b;
    private final ConcurrentHashMap<String, CompletableFuture<String>> results = new ConcurrentHashMap<>();
    private final ScheduledExecutorService scheduler = Executors.newScheduledThreadPool(1);

    public TestHttpClient(String host, int port, int defaultTimeout) {
        this.host = host;
        this.port = port;
        this.defaultTimeout = defaultTimeout;
        this.workerGroup = new NioEventLoopGroup();
        this.b = new Bootstrap();
        init();
    }

    private void init() {
        b.group(workerGroup)
         .channel(NioSocketChannel.class)
         .handler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) {
                 ch.pipeline().addLast(new HttpClientCodec());
                 ch.pipeline().addLast(new HttpObjectAggregator(65536));
                 ch.pipeline().addLast(new HttpResponseHandler());
             }
         });
    }

    public void close() {
        scheduler.shutdownNow();
        workerGroup.shutdownGracefully();
    }

    public CompletableFuture<String> get(String uri) {
        return send(buildGetRequest(uri));
    }

    public CompletableFuture<String> post(String uri, Map<String, String> params, Map<String, String> headers) {
        return send(buildPostRequest(uri, params, headers));
    }

    private CompletableFuture<String> send(HttpRequest request) {
        String requestId = UUID.randomUUID().toString();
        CompletableFuture<String> future = new CompletableFuture<>();
        results.put(requestId, future);

        scheduler.schedule(() -> {
            if (!future.isDone()) {
                future.completeExceptionally(new TimeoutException("Request timeout"));
                results.remove(requestId);
            }
        }, defaultTimeout, TimeUnit.MILLISECONDS);

        b.connect(host, port).addListener((ChannelFutureListener) future1 -> {
            if (future1.isSuccess()) {
                Channel ch = future1.channel();
                ch.writeAndFlush(request).addListener(f -> {
                    if (!f.isSuccess()) {
                        future.completeExceptionally(f.cause());
                        results.remove(requestId);
                    }
                });
            } else {
                future.completeExceptionally(future1.cause());
                results.remove(requestId);
            }
        });

        return future;
    }

    private FullHttpRequest buildGetRequest(String uri) {
        URI uriObj = URI.create(uri);
        String fullPath = uriObj.getRawPath() + (uriObj.getRawQuery() != null ? "?" + uriObj.getRawQuery() : "");
        DefaultFullHttpRequest request = new DefaultFullHttpRequest(HttpVersion.HTTP_1_1, HttpMethod.GET, fullPath);
        request.headers().set(HttpHeaderNames.HOST, host + ":" + port);
        request.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.KEEP_ALIVE);
        return request;
    }

    private FullHttpRequest buildPostRequest(String uri, Map<String, String> params, Map<String, String> headers) {
        URI uriObj = URI.create(uri);
        String fullPath = uriObj.getRawPath();
        DefaultFullHttpRequest request = new DefaultFullHttpRequest(HttpVersion.HTTP_1_1, HttpMethod.POST, fullPath);
        request.headers().set(HttpHeaderNames.HOST, host + ":" + port);
        request.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.KEEP_ALIVE);
        request.headers().set(HttpHeaderNames.CONTENT_TYPE, "application/x-www-form-urlencoded");
        request.headers().set(HttpHeaderNames.CONTENT_LENGTH, params.size() * 3);

        StringBuilder formData = new StringBuilder();
        for (Map.Entry<String, String> entry : params.entrySet()) {
            if (formData.length() > 0) formData.append("&");
            formData.append(entry.getKey()).append("=").append(entry.getValue());
        }

        request.content().writeBytes(formData.toString().getBytes(CharsetUtil.UTF_8));
        if (headers != null) {
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                request.headers().set(entry.getKey(), entry.getValue());
            }
        }
        return request;
    }

    private class HttpResponseHandler extends SimpleChannelInboundHandler<FullHttpResponse> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, FullHttpResponse response) {
            String requestId = ctx.channel().attr(AttributeKey.valueOf("requestId")).get();
            CompletableFuture<String> future = results.get(requestId);
            if (future == null) {
                ctx.close();
                return;
            }

            try {
                String content = response.content().toString(CharsetUtil.UTF_8);
                future.complete(content);
            } catch (Exception e) {
                future.completeExceptionally(e);
            } finally {
                results.remove(requestId);
                ctx.close();
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            String requestId = ctx.channel().attr(AttributeKey.valueOf("requestId")).get();
            CompletableFuture<String> future = results.get(requestId);
            if (future != null) {
                future.completeExceptionally(cause);
                results.remove(requestId);
            }
            ctx.close();
        }
    }
}

