public class FileChannelInteractor {
    private FileChannel fileChannel;
    private MappedByteBuffer mappedBuffer;

    public void openFileChannel(String filePath) throws IOException {
        Path path = Paths.get(filePath);
        this.fileChannel = FileChannel.open(path, StandardOpenOption.READ);
    }

    public void mapFileToMemory(long position, long size) throws IOException {
        this.mappedBuffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, position, size);
    }

    public MappedByteBuffer getMappedBuffer() {
        return this.mappedBuffer;
    }

    public void closeChannel() throws IOException {
        if (this.fileChannel != null) {
            this.fileChannel.close();
        }
    }

    public void forceWriteToDisk() {
        if (this.mappedBuffer != null) {
            this.mappedBuffer.force();
        }
    }
}

