        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(acceptorEventLoopGroup, networkEventLoopGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(applicationContext.getBean("decoderHandler"));
                        pipeline.addLast(applicationContext.getBean("encoderHandler"));
                        pipeline.addLast(eventExecutorGroup, applicationContext.getBean("businessHandler"));
                    }
                })
                .option(ChannelOption.SO_BACKLOG, 128)
                .childOption(ChannelOption.SO_KEEPALIVE, true);

        return bootstrap.bind(config.getPort());
    }
}

