                        finishConnect(key, key.attachment());
                    }
                }
                connect(selector);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void connect(Selector selector) {
        BackendConnection c;
        while ((c = connectQueue.poll()) != null) {
            try {
                SocketChannel channel = c.getChannel();
                channel.configureBlocking(false);
                channel.connect(c.getAddress());
                SelectionKey key = channel.register(selector, SelectionKey.OP_CONNECT, c);
                key.attach(c);
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void finishConnect(SelectionKey key, Object att) {
        BackendConnection c = (BackendConnection) att;
        SocketChannel channel = (SocketChannel) key.channel();
        try {
            if (channel.finishConnect()) {
                connectCount++;
                int id = ConnectIdGenerator.nextId();
                c.setId(id);
                NIOProcessor processor = nextProcessor();
                processor.handleConnection(c);
                key.interestOps(0);
            } else {
                key.cancel();
                channel.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            clearSelectionKey(key);
        }
    }

    private void clearSelectionKey(SelectionKey key) {
        key.cancel();
        try {
            key.channel().close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private NIOProcessor nextProcessor() {
        NIOProcessor processor = processors[nextProcessor];
        nextProcessor = (nextProcessor + 1) % processors.length;
        return processor;
    }

    public static class ConnectIdGenerator {
        private static final AtomicInteger idCounter = new AtomicInteger(0);

        public static int nextId() {
            return idCounter.incrementAndGet();
        }
    }
}

