import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.ServerSocketChannel;
import java.nio.channels.SocketChannel;
import java.nio.charset.Charset;
import java.util.Iterator;
import java.util.Set;

public class NioHttpServer {

    private ServerSocketChannel serverSocketChannel;
    private Selector selector;
    private Selector clientReadSelector;
    private Selector clientWriteSelector;

    public NioHttpServer(int port) throws IOException {
        serverSocketChannel = ServerSocketChannel.open();
        selector = Selector.open();
        clientReadSelector = Selector.open();
        clientWriteSelector = Selector.open();

        serverSocketChannel.socket().bind(new InetSocketAddress(port));
        serverSocketChannel.configureBlocking(false);
        serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);
    }

    public void start() {
        new Thread(new ServerChannelThread(selector, clientReadSelector)).start();
        new Thread(new ClientReadChannelThread(clientReadSelector, clientWriteSelector)).start();
        new Thread(new ClientWriteChannelThread(clientReadSelector, clientWriteSelector)).start();
    }

    private static class ServerChannelThread implements Runnable {
        private Selector serverSelector;
        private Selector clientReadSelector;

        public ServerChannelThread(Selector serverSelector, Selector clientReadSelector) {
            this.serverSelector = serverSelector;
            this.clientReadSelector = clientReadSelector;
        }

        @Override
        public void run() {
            try {
                while (true) {
                    if (serverSelector.select(1) > 0) {
                        Set<SelectionKey> set = serverSelector.selectedKeys();
                        Iterator<SelectionKey> keyIterator = set.iterator();

                        while (keyIterator.hasNext()) {
                            SelectionKey key = keyIterator.next();
                            if (key.isAcceptable()) {
                                SocketChannel clientChannel = ((ServerSocketChannel) key.channel()).accept();
                                clientChannel.configureBlocking(false);
                                clientChannel.register(clientReadSelector, SelectionKey.OP_READ);
                            }
                            keyIterator.remove();
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private static class ClientReadChannelThread implements Runnable {
        private Selector clientReadSelector;
        private Selector clientWriteSelector;

        public ClientReadChannelThread(Selector clientReadSelector, Selector clientWriteSelector) {
            this.clientReadSelector = clientReadSelector;
            this.clientWriteSelector = clientWriteSelector;
        }

        @Override
        public void run() {
            try {
                while (true) {
                    if (clientReadSelector.select(1) > 0) {
                        Set<SelectionKey> set = clientReadSelector.selectedKeys();
                        Iterator<SelectionKey> keyIterator = set.iterator();

                        while (keyIterator.hasNext()) {
                            SelectionKey key = keyIterator.next();

                            if (key.isReadable()) {
                                try {
                                    SocketChannel clientChannel = (SocketChannel) key.channel();
                                    ByteBuffer byteBuffer = ByteBuffer.allocate(1024);
                                    clientChannel.read(byteBuffer);
                                    byteBuffer.flip();
                                    System.out.println(Charset.defaultCharset().newDecoder().decode(byteBuffer).toString());
                                    clientChannel.register(clientWriteSelector, SelectionKey.OP_WRITE);
                                } finally {
                                    keyIterator.remove();
                                }
                            }
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private static class ClientWriteChannelThread implements Runnable {
        private Selector clientReadSelector;
        private Selector clientWriteSelector;

        public ClientWriteChannelThread(Selector clientReadSelector, Selector clientWriteSelector) {
            this.clientReadSelector = clientReadSelector;
            this.clientWriteSelector = clientWriteSelector;
        }

        @Override
        public void run() {
            try {
                while (true) {
                    if (clientWriteSelector.select(1) > 0) {
                        Set<SelectionKey> set = clientWriteSelector.selectedKeys();
                        Iterator<SelectionKey> keyIterator = set.iterator();

                        while (keyIterator.hasNext()) {
                            SelectionKey key = keyIterator.next();

                            if (key.isWritable()) {
                                try {
                                    SocketChannel clientChannel = (SocketChannel) key.channel();
                                    ByteBuffer byteBuffer = ByteBuffer.allocate(1024);
                                    String msg = "server channel write: current time mill " + System.currentTimeMillis();
                                    byteBuffer.put(msg.getBytes());
                                    byteBuffer.flip();
                                    clientChannel.write(byteBuffer);
                                    clientChannel.register(clientReadSelector, SelectionKey.OP_READ);
                                } finally {
                                    keyIterator.remove();
                                    key.interestOps(SelectionKey.OP_READ);
                                }
                            }
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

