        private volatile Integer mCount = 0;
        private volatile Object mLock = new Object();

        public Integer size() {
            synchronized(mLock) {
                return mCount;
            }
        }

        public Integer encrease() {
            synchronized(mLock) {
                return (mCount += 1);
            }
        }

        public Integer decrease() {
            synchronized(mLock) {
                return mCount > 0 ? (mCount -= 1) : (mCount = 0);
            }
        }

        public void reset() {
            synchronized(mLock) {
                mCount = 0;
            }
        }
    }

