    private class AsyncPipe extends Thread {
        private final InputStream is;
        private final OutputStream os;
        private final boolean closeStreams;

        public AsyncPipe(InputStream is, OutputStream os) {
            this(is, os, true);
        }

        public AsyncPipe(InputStream is, OutputStream os, boolean closeStreams) {
            this.is = is;
            this.os = os;
            this.closeStreams = closeStreams;
        }

        public void run() {
            try {
                byte[] buffer = new byte[8192];
                int bytesRead;
                while ((bytesRead = is.read(buffer)) != -1) {
                    os.write(buffer, 0, bytesRead);
                }
                os.flush();
                if (closeStreams) {
                    is.close();
                    os.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
