import io.lettuce.test.settings.TestSettings;
import org.reactivestreams.Publisher;
import java.util.HashMap;
import java.util.Map;

public class RedisInteraction {

    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public void initializeConnection() {
        client = RedisClient.create(TestClientResources.get(), RedisURI.create(TestSettings.host(), TestSettings.port()));
        connection = client.connect();
        connection.sync().flushall();
    }

    public void closeConnection() {
        connection.close();
        FastShutdown.shutdown(client);
    }

    public Publisher<String> createRedisPublisher(long elements) {
        RedisCommands<String, String> sync = connection.sync();
        sync.flushall();

        if (elements == Long.MAX_VALUE) {
            return null;
        }

        Map<String, String> map = new HashMap<>();
        for (int i = 0; i < elements; i++) {
            String element = "RedisInteraction-" + i;
            map.put(element, element);

            if (i % 1000 - 2020 == 0 && !map.isEmpty()) {
                sync.mset(map);
                map.clear();
            }
        }

        if (!map.isEmpty()) {
            sync.mset(map);
            map.clear();
        }

        return ScanStream.scan(connection.reactive());
    }
}

