        } catch (IOException e) {
            throw new RuntimeException("Failed to write data", e);
        }
    }

    private void rollover() {
        closeDataChunk();
        try {
            mFileChannel.close();
        } catch (IOException e) {
            throw new RuntimeException("Failed to close file channel", e);
        }
        updateFileName();
        try {
            mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE_NEW, StandardOpenOption.WRITE);
            writeHeader();
            mDataChunkOpen = true;
            mDataChunkSize = 0;
            mDataChunkSizeOffset = 32;
        } catch (IOException e) {
            throw new RuntimeException("Failed to open new file after rollover", e);
        }
    }

    private void closeDataChunk() {
        updateDataChunkSize();
        updateTotalSize();
    }

    private void updateDataChunkSize() {
        try {
            mFileChannel.position(mDataChunkSizeOffset);
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
            mFileChannel.write(sizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update data chunk size", e);
        }
    }

    private void updateTotalSize() {
        try {
            mFileChannel.position(4);
            long totalSize = 28 + mDataChunkSize;
            ByteBuffer totalSizeBuffer = getUnsignedIntegerBuffer(totalSize);
            mFileChannel.write(totalSizeBuffer);
        } catch (IOException e) {
            throw new RuntimeException("Failed to update total size", e);
        }
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        buffer.putInt((int) size);
        buffer.flip();
        return buffer;
    }

    private void updateFileName() {
        String baseName = mFile.getFileName().toString();
        int dotIndex = baseName.lastIndexOf('.');
        String namePart = dotIndex == -1 ? baseName : baseName.substring(0, dotIndex);
        String extension = dotIndex == -1 ? "" : baseName.substring(dotIndex);
        mFile = mFile.getParent().resolve(namePart + "_" + mFileRolloverCounter + extension);
        mFileRolloverCounter++;
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer buffer = ByteBuffer.allocate(8);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("data".getBytes());
        buffer.putInt(0);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(12);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("RIFF".getBytes());
        buffer.putInt(28);
        buffer.put("WAVE".getBytes());
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(16);
        buffer.order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("fmt ".getBytes());
        buffer.putInt(16);
        buffer.putShort((short) 1);
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) format.getSampleRate());
        buffer.putInt((int) (format.getSampleRate() * format.getChannels() * (format.getSampleSizeInBits() / 8)));
        buffer.putShort((short) (format.getChannels() * (format.getSampleSizeInBits() / 8)));
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.flip();
        return buffer;
    }

    @Override
    public void close() {
        closeDataChunk();
        try {
            mFileChannel.close();
        } catch (IOException e) {
            throw new RuntimeException("Failed to close file channel", e);
        }
    }

    public void close(Path path) {
        try {
            mFileChannel.close();
            Files.move(mFile, path, StandardCopyOption.REPLACE_EXISTING);
        } catch (IOException e) {
            throw new RuntimeException("Failed to close and rename file", e);
        }
    }
}

