    }

    public void sendToNetwork(IPv4Packet packet) {
        clientToNetwork.write(packet.getBytes(), packet.getLength());
        updateInterests();
    }

    private void updateInterests() {
        int newOps = 0;
        if (mayRead()) {
            newOps |= SelectionKey.OP_READ;
        }
        if (mayWrite()) {
            newOps |= SelectionKey.OP_WRITE;
        }
        if (mayConnect()) {
            newOps |= SelectionKey.OP_CONNECT;
        }
        if (newOps != interests) {
            selectionKey.interestOps(newOps);
        }
    }

    private boolean mayRead() {
        return channel != null && channel.isConnected() && networkToClient.isReady();
    }

    private boolean mayWrite() {
        return channel != null && channel.isConnected() && clientToNetwork.hasData();
    }

    private boolean mayConnect() {
        return channel != null && !channel.finishConnect();
    }
}

