                log.log("Receiver thread interrupted: " + e.getMessage());
            } finally {
                try {
                    if (client_channel != null) {
                        client_channel.close();
                    }
                } catch (IOException e) {
                    log.log("Error closing channel: " + e.getMessage());
                }
            }
        }
    }

    public interface RtReceiver {
        void onReceive(ByteBuffer buf);
    }
