    private boolean mConnectionLoggingSuppressed;

    public IcecastBroadcastMetadataUpdater(IcecastConfiguration icecastConfiguration) {
        this.mIcecastConfiguration = icecastConfiguration;
        this.mHttpClient = HttpClient.newHttpClient();
        this.mLog = LoggerFactory.getLogger(IcecastBroadcastMetadataUpdater.class);
        this.mConnectionLoggingSuppressed = false;
    }

    public void update(IdentifierCollection identifierCollection) {
        try {
            String host = mIcecastConfiguration.getHost();
            int port = mIcecastConfiguration.getPort();
            String mountPoint = mIcecastConfiguration.getMountPoint();
            String password = mIcecastConfiguration.getPassword();

            String encodedPassword = URLEncoder.encode(password, StandardCharsets.UTF_8.toString());
            String urlString = String.format("http://%s:%d%s?password=%s", host, port, mountPoint, encodedPassword);
            URI uri = URI.create(urlString);

            StringBuilder bodyBuilder = new StringBuilder();
            for (Map.Entry<String, String> entry : identifierCollection.getEntries()) {
                if (bodyBuilder.length() > 0) {
                    bodyBuilder.append('&');
                }
                String encodedKey = URLEncoder.encode(entry.getKey(), StandardCharsets.UTF_8.toString());
                String encodedValue = URLEncoder.encode(entry.getValue(), StandardCharsets.UTF_8.toString());
                bodyBuilder.append(encodedKey).append('=').append(encodedValue);
            }
            String body = bodyBuilder.toString();

            HttpRequest request = HttpRequest.newBuilder()
                    .uri(uri)
                    .header("Content-Type", "application/x-www-form-urlencoded")
                    .POST(HttpRequest.BodyPublishers.ofString(body))
                    .build();

            mHttpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString())
                    .thenApply(response -> {
                        if (response.statusCode() == 200) {
                            mLog.info("Metadata update successful");
                        } else {
                            if (!mConnectionLoggingSuppressed) {
                                mLog.error("Failed to update metadata: {}", response.statusCode());
                                mConnectionLoggingSuppressed = true;
                            }
                        }
                        return null;
                    })
                    .exceptionally(ex -> {
                        if (!mConnectionLoggingSuppressed) {
                            mLog.error("Exception during metadata update", ex);
                            mConnectionLoggingSuppressed = true;
                        }
                        return null;
                    });
        } catch (Exception e) {
            mLog.error("Error constructing metadata update request", e);
        }
    }
}

