public class TcpServer extends Thread {
    private IAppFrame frame;
    private int port;
    private String file;

    public TcpServer(int port, IAppFrame frame) {
        this.port = port;
        this.frame = frame;
    }

    public TcpServer(int port, IAppFrame frame, String file) {
        this(port, frame);
        this.file = file;
        if (file != null) {
            frame.openFile(file);
        }
    }

    public static boolean checkExistInstance(int port) {
        try (Socket socket = new Socket()) {
            socket.connect(new InetSocketAddress("localhost", port), 1000);
            return true;
        } catch (IOException e) {
            return false;
        }
    }

    private static boolean ask(String host, String cmd, int port) {
        try (Socket socket = new Socket(host, port);
             PrintWriter out = new PrintWriter(socket.getOutputStream(), true);
             BufferedReader in = new BufferedReader(new InputStreamReader(socket.getInputStream()))) {
            out.println(cmd);
            String response = in.readLine();
            return "OK".equals(response);
        } catch (IOException e) {
            return false;
        }
    }

    @Override
    public void run() {
        try (ServerSocket serverSocket = new ServerSocket(port)) {
            while (true) {
                Socket clientSocket = serverSocket.accept();
                new Thread(new ClientHandler(clientSocket, frame)).start();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private class ClientHandler implements Runnable {
        private Socket socket;
        private IAppFrame frame;

        public ClientHandler(Socket socket, IAppFrame frame) {
            this.socket = socket;
            this.frame = frame;
        }

        @Override
        public void run() {
            try (BufferedReader in = new BufferedReader(new InputStreamReader(socket.getInputStream()));
                 PrintWriter out = new PrintWriter(socket.getOutputStream(), true)) {

                String command = in.readLine();
                if (command == null) return;

                if (command.equals("getWindowTitles")) {
                    List<String> titles = frame.getWindowTitles();
                    out.println(String.join(",", titles));
                } else if (command.startsWith("activateWindow ")) {
                    String title = command.substring("activateWindow ".length());
                    frame.activateWindow(title);
                    out.println("OK");
                } else if (command.startsWith("openFile ")) {
                    String filePath = command.substring("openFile ".length());
                    frame.openFile(filePath);
                    out.println("OK");
                } else {
                    out.println("Unknown command");
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

