import java.io.ObjectOutputStream;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.SocketException;
import java.net.UnknownHostException;
import java.util.concurrent.atomic.AtomicReference;

public final class BinaryLinkServer {

    private final Acceptor acceptor;
    private final AtomicReference<Handler> handler;

    public BinaryLinkServer() throws IOException {
        acceptor = new Acceptor();
        acceptor.start();
        handler = new AtomicReference<>();
    }

    public void terminate() {
        acceptor.close();
        Handler h = handler.getAndSet(null);
        if (h != null) {
            h.close();
        }
        try {
            acceptor.join();
            if (h != null) {
                h.join();
            }
        } catch (InterruptedException e) {
            // ignore
        }
    }

    private InetAddress getListenAddress() throws UnknownHostException {
        String addr = System.getProperty("jmh.link.address");
        if (addr != null) {
            return InetAddress.getByName(addr);
        }
        try {
            Method m = InetAddress.class.getMethod("getLoopbackAddress");
            return (InetAddress) m.invoke(null);
        } catch (Exception e) {
            // fall through
        }
        return InetAddress.getByAddress(new byte[] {127, 0, 0, 1});
    }

    private int getListenPort() {
        return Integer.getInteger("jmh.link.port", 0);
    }

    private final class Acceptor extends Thread {
        private final ServerSocket server;
        private final InetAddress listenAddress;

        public Acceptor() throws IOException {
            listenAddress = getListenAddress();
            server = new ServerSocket(getListenPort(), 50, listenAddress);
        }

        @Override
        public void run() {
            try {
                while (!Thread.interrupted()) {
                    Socket clientSocket = server.accept();
                    Handler r = new Handler(clientSocket);
                    if (!handler.compareAndSet(null, r)) {
                        throw new IllegalStateException("Handler already registered");
                    }
                    r.start();
                }
            } catch (SocketException e) {
                // assume "Socket closed"
            } catch (IOException e) {
                throw new IllegalStateException(e);
            } finally {
                close();
            }
        }

        public void close() {
            try {
                server.close();
            } catch (IOException e) {
                // ignore
            }
        }
    }

    private final class Handler extends Thread {
        private final Socket socket;
        private final InputStream is;
        private final OutputStream os;
        private ObjectInputStream ois;
        private ObjectOutputStream oos;

        public Handler(Socket socket) throws IOException {
            this.socket = socket;
            this.is = socket.getInputStream();
            this.os = socket.getOutputStream();
            this.oos = new ObjectOutputStream(new BufferedOutputStream(os));
            oos.flush();
        }

        @Override
        public void run() {
            try {
                ois = new ObjectInputStream(new BufferedInputStream(is));
                while (true) {
                    Object obj = ois.readObject();
                    if (obj == null) break;
                    // Handle different frame types here
                }
            } catch (Exception e) {
                // Handle exceptions
            } finally {
                close();
            }
        }

        public void close() {
            try {
                socket.close();
            } catch (IOException e) {
                // ignore
            }
        }
    }
}

