            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public void process(Socket clnt) {
        try {
            InputStream in = clnt.getInputStream();
            OutputStream out = clnt.getOutputStream();

            String requestLine = readLine(in);
            String[] parts = requestLine.split(" ");
            String method = parts[0];
            String path = parts[1];

            if (method.equals("GET")) {
                doReply(in, out, path);
            } else {
                replyError(out, new Exception("Unsupported method: " + method));
            }

            clnt.close();
        } catch (IOException e) {
            replyError(clnt.getOutputStream(), e);
        }
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\r') {
                int next = in.read();
                if (next == '\n') {
                    break;
                }
            }
            sb.append((char) c);
        }
        return sb.toString();
    }

    private int skipLine(InputStream in) throws IOException {
        int length = 0;
        int c;
        while ((c = in.read()) != -1) {
            length++;
            if (c == '\r') {
                int next = in.read();
                if (next == '\n') {
                    return length;
                }
            }
        }
        return length;
    }

    private void doReply(InputStream in, OutputStream out, String cmd) throws IOException {
        if (cmd.equals("/")) {
            String response = "HTTP/1.1 200 OK\r\nContent-Type: text/html\r\nContent-Length: 12\r\n\r\nHello, World!";
            out.write(response.getBytes());
        } else {
            String response = "HTTP/1.1 404 Not Found\r\nContent-Type: text/plain\r\nContent-Length: 9\r\n\r\nNot Found";
            out.write(response.getBytes());
        }
    }

    private void replyError(OutputStream out, Exception e) throws IOException {
        String message = "HTTP/1.1 500 Internal Server Error\r\nContent-Type: text/plain\r\nContent-Length: " + e.getMessage().length() + "\r\n\r\n" + e.getMessage();
        out.write(message.getBytes());
    }
}

