    public void run() {
        final ServerSocket ss = serverSocket;
        while (ss == serverSocket) {
            Socket conn = null;
            try {
                conn = ss.accept();
                String method = null;
                String path = null;
                HashMap<String, String> headers = new HashMap<String, String>();

                InputStream ins = conn.getInputStream();
                StringBuilder sb = new StringBuilder(512);
                int l;
