    private void upgradeToTls(Socket socket) throws Exception {
        SSLContext sslContext = SSLContext.getInstance("TLS");
        KeyStore keyStore = keyStoreProvider.getKeyStore();
        KeyManager[] keyManagers = getKeyManagers(keyStore, keyStoreProvider.getPassword());
        TrustManager[] trustManagers = getTrustManagers(keyStore, keyStoreProvider.getPassword());
        sslContext.init(keyManagers, trustManagers, null);
        SSLSocket sslSocket = (SSLSocket) sslContext.getSocketFactory().createSocket(socket, socket.getInetAddress().getHostAddress(), socket.getPort(), true);
        sslSocket.startHandshake();
        socket = sslSocket;
    }

    private KeyManager[] getKeyManagers(KeyStore keyStore, char[] password) throws Exception {
        KeyManagerFactory kmf = KeyManagerFactory.getInstance(KeyManagerFactory.getDefaultAlgorithm());
        kmf.init(keyStore, password);
        return kmf.getKeyManagers();
    }

    private TrustManager[] getTrustManagers(KeyStore keyStore, char[] password) throws Exception {
        TrustManagerFactory tmf = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
        tmf.init(keyStore);
        return tmf.getTrustManagers();
    }

    public boolean shouldStop() {
        return mockServerThread.isInterrupted();
    }

    private class MockServerThread extends Thread {
        private final ServerSocket serverSocket;
        private final Deque<SmtpInteraction> interactions;
        private final CountDownLatch waitForConnectionClosed;
        private final CountDownLatch waitForAllExpectedCommands;
        private final Logger logger;
        private final KeyStoreProvider keyStoreProvider;

        public MockServerThread(ServerSocket serverSocket, Deque<SmtpInteraction> interactions, CountDownLatch waitForConnectionClosed, CountDownLatch waitForAllExpectedCommands, Logger logger, KeyStoreProvider keyStoreProvider) {
            this.serverSocket = serverSocket;
            this.interactions = interactions;
            this.waitForConnectionClosed = waitForConnectionClosed;
            this.waitForAllExpectedCommands = waitForAllExpectedCommands;
            this.logger = logger;
            this.keyStoreProvider = keyStoreProvider;
        }

        public void setServerSocket(ServerSocket serverSocket) {
            this.serverSocket = serverSocket;
        }

        @Override
        public void run() {
            try {
                while (!shouldStop()) {
                    Socket clientSocket = serverSocket.accept();
                    logger.logInfo("Client connected: " + clientSocket.getInetAddress());
                    try {
                        processInteractions(clientSocket);
                    } catch (Exception e) {
                        logger.logError("Error processing client interactions", e);
                    } finally {
                        clientSocket.close();
                        waitForConnectionClosed.countDown();
                    }
                }
            } catch (IOException e) {
                logger.logError("Server socket error", e);
            }
        }

        private void processInteractions(Socket socket) throws Exception {
            Iterator<SmtpInteraction> interactionIterator = interactions.iterator();
            while (interactionIterator.hasNext() && !shouldStop()) {
                SmtpInteraction interaction = interactionIterator.next();
                if (interaction instanceof ExpectedCommand) {
                    readExpectedCommand((ExpectedCommand) interaction);
                } else if (interaction instanceof CannedResponse) {
                    writeCannedResponse((CannedResponse) interaction);
                } else if (interaction instanceof UpgradeToTls) {
                    upgradeToTls(socket);
                }
                interactionIterator.remove();
            }
            waitForAllExpectedCommands.countDown();
        }
    }

    public interface SmtpInteraction {}

    public static class ExpectedCommand implements SmtpInteraction {
        private final String command;

        public ExpectedCommand(String command) {
            this.command = command;
        }

        public String getCommand() {
            return command;
        }
    }

    public static class CannedResponse implements SmtpInteraction {
        private final String response;

        public CannedResponse(String response) {
            this.response = response;
        }

        public String getResponse() {
            return response;
        }
    }

    public static class UpgradeToTls implements SmtpInteraction {}

    public interface Logger {
        void logInfo(String message);
        void logError(String message, Exception e);
    }

    public interface KeyStoreProvider {
        KeyStore getKeyStore();
        char[] getPassword();
    }
}

