import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.Socket;
import java.net.SocketException;
import java.net.SocketTimeoutException;
import java.net.UnknownHostException;

public class TelnetTransport {
    private Socket socket;
    private InputStream is;
    private OutputStream os;
    private boolean connected = false;

    public void connect(String hostname, int port) throws IOException {
        socket = new Socket();
        tryAllAddresses(socket, hostname, port);
        connected = true;
        is = socket.getInputStream();
        os = socket.getOutputStream();
    }

    private void tryAllAddresses(Socket sock, String host, int port) throws IOException {
        InetAddress[] addresses = InetAddress.getAllByName(host);
        for (InetAddress addr : addresses) {
            try {
                sock.connect(new InetSocketAddress(addr, port));
                return;
            } catch (SocketTimeoutException ignored) {
            }
        }
        throw new SocketTimeoutException("Could not connect; socket timed out");
    }

    public void close() {
        connected = false;
        if (socket != null) {
            try {
                socket.close();
                socket = null;
            } catch (IOException e) {
                System.err.println("Error closing socket: " + e.getMessage());
            }
        }
    }

    public void flush() throws IOException {
        if (os != null) {
            os.flush();
        }
    }

    public boolean isConnected() {
        return connected;
    }

    public int read(byte[] buffer, int start, int len) throws IOException {
        int n = is.read(buffer, start, len);
        if (n < 0) {
            throw new IOException("Remote end closed connection.");
        }
        return n;
    }

    public void write(byte[] buffer) throws IOException {
        if (os != null) {
            os.write(buffer);
        }
    }

    public void write(int c) throws IOException {
        if (os != null) {
            os.write(c);
        }
    }
}

