        socket.bind(new InetSocketAddress(localHost, localPort));
        if (proxyType == Proxy.Type.HTTP) {
            connectToProxy(socket, host, port);
        } else {
            socket.connect(new InetSocketAddress(host, port));
        }
        return socket;
    }

    @Override
    public Socket createSocket(InetAddress host, int port) throws IOException {
        Socket socket = createSocket();
        if (proxyType != Proxy.Type.DIRECT) {
            Proxy proxy = new Proxy(proxyType, new InetSocketAddress(proxyHost, proxyPort));
            socket = new Socket(proxy);
        } else {
            socket = new Socket();
        }
        if (proxyType == Proxy.Type.HTTP) {
            connectToProxy(socket, host.getHostAddress(), port);
        } else {
            socket.connect(new InetSocketAddress(host, port));
        }
        return socket;
    }

    @Override
    public Socket createSocket(InetAddress address, int port, InetAddress localAddress, int localPort) throws IOException {
        Socket socket = createSocket();
        if (proxyType != Proxy.Type.DIRECT) {
            Proxy proxy = new Proxy(proxyType, new InetSocketAddress(proxyHost, proxyPort));
            socket = new Socket(proxy);
        } else {
            socket = new Socket();
        }
        socket.bind(new InetSocketAddress(localAddress, localPort));
        if (proxyType == Proxy.Type.HTTP) {
            connectToProxy(socket, address.getHostAddress(), port);
        } else {
            socket.connect(new InetSocketAddress(address, port));
        }
        return socket;
    }

    @Override
    public Socket createSocket() throws IOException {
        Socket socket;
        if (proxyType != Proxy.Type.DIRECT) {
            Proxy proxy = new Proxy(proxyType, new InetSocketAddress(proxyHost, proxyPort));
            socket = new Socket(proxy);
        } else {
            socket = new Socket();
        }
        return socket;
    }

    private void connectToProxy(Socket socket, String host, int port) throws IOException {
        OutputStream out = socket.getOutputStream();
        InputStream in = socket.getInputStream();
        String request = "CONNECT " + host + ":" + port + " HTTP/1.1\r\nHost: " + host + ":" + port + "\r\n\r\n";
        out.write(request.getBytes(StandardCharsets.UTF_8));
        out.flush();

        String statusLine = readLine(in);
        int responseCode = getResponseCode(statusLine);
        if (responseCode == 407) {
            String authStr = getBasicAuthStr();
            String authHeader = "Proxy-Authorization: Basic " + authStr + "\r\n";
            out.write(authHeader.getBytes(StandardCharsets.UTF_8));
            out.flush();
            statusLine = readLine(in);
            responseCode = getResponseCode(statusLine);
        }
        if (responseCode != 200) {
            throw new IOException("HTTP proxy connect failed: " + statusLine);
        }
    }

    private String getBasicAuthStr() {
        String credentials = proxyUser + ":" + proxyPass;
        return Base64.getEncoder().encodeToString(credentials.getBytes(StandardCharsets.UTF_8));
    }

    private String readLine(InputStream in) throws IOException {
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\n') {
                break;
            }
            sb.append((char) c);
        }
        return sb.toString();
    }

    private static int getResponseCode(String statusLine) {
        String[] parts = statusLine.split(" ");
        if (parts.length >= 1) {
            try {
                return Integer.parseInt(parts[1]);
            } catch (NumberFormatException e) {
                return -1;
            }
        }
        return -1;
    }
}

