  private Statsd() {
    e.submit(
        () -> {
          DatagramSocket ds = null;
          boolean entered = BTraceRuntime.enter();
          try {
            ds = new DatagramSocket();
            DatagramPacket dp = new DatagramPacket(new byte[0], 0);
