import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;
import java.util.Map;
import java.util.logging.Logger;

public class TinyIdHttpUtils {
    private static final Logger logger = Logger.getLogger(TinyIdHttpUtils.class.getName());

    public static String post(String url, Integer readTimeout, Integer connectTimeout) {
        try {
            URL urlObj = new URL(url);
            HttpURLConnection connection = (HttpURLConnection) urlObj.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);

            if (readTimeout != null) {
                connection.setReadTimeout(readTimeout);
            }
            if (connectTimeout != null) {
                connection.setConnectTimeout(connectTimeout);
            }

            try (OutputStream os = connection.getOutputStream()) {
                // No data to write
            }

            int responseCode = connection.getResponseCode();
            if (responseCode >= 200 && responseCode < 300) {
                try (InputStream is = connection.getInputStream()) {
                    return readStream(is);
                }
            } else {
                try (InputStream errorStream = connection.getErrorStream()) {
                    if (errorStream != null) {
                        return readStream(errorStream);
                    }
                }
                return "";
            }
        } catch (IOException e) {
            logger.severe("Error during POST request to " + url + ": " + e.getMessage());
            return "";
        }
    }

    public static String post(String url, Map<String, String> form, Integer readTimeout, Integer connectTimeout) {
        try {
            URL urlObj = new URL(url);
            HttpURLConnection connection = (HttpURLConnection) urlObj.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);

            if (readTimeout != null) {
                connection.setReadTimeout(readTimeout);
            }
            if (connectTimeout != null) {
                connection.setConnectTimeout(connectTimeout);
            }

            StringBuilder formData = new StringBuilder();
            for (Map.Entry<String, String> entry : form.entrySet()) {
                if (formData.length() > 0) {
                    formData.append('&');
                }
                String encodedKey = URLEncoder.encode(entry.getKey(), "UTF-8");
                String encodedValue = URLEncoder.encode(entry.getValue(), "UTF-8");
                formData.append(encodedKey).append('=').append(encodedValue);
            }

            String formDataStr = formData.toString();
            connection.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
            connection.setRequestProperty("Content-Length", String.valueOf(formDataStr.getBytes(StandardCharsets.UTF_8).length));

            try (OutputStream os = connection.getOutputStream()) {
                os.write(formDataStr.getBytes(StandardCharsets.UTF_8));
            }

            int responseCode = connection.getResponseCode();
            if (responseCode >= 200 && responseCode < 300) {
                try (InputStream is = connection.getInputStream()) {
                    return readStream(is);
                }
            } else {
                try (InputStream errorStream = connection.getErrorStream()) {
                    if (errorStream != null) {
                        return readStream(errorStream);
                    }
                }
                return "";
            }
        } catch (IOException e) {
            logger.severe("Error during POST request to " + url + ": " + e.getMessage());
            return "";
        }
    }

    private static String readStream(InputStream is) throws IOException {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, StandardCharsets.UTF_8))) {
            StringBuilder sb = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line);
            }
            return sb.toString();
        }
    }
}
