import java.net.URL;
import java.util.Map;
import java.util.HashMap;
import java.util.stream.Collectors;
import com.google.gson.Gson;

public class ApiHandler {
    private String baseUrl;
    private Map<String, String> additionalHeaders;

    public ApiHandler(String url) {
        this.baseUrl = url;
        this.additionalHeaders = new HashMap<>();
    }

    public ApiHandler(String url, String apiKey) {
        this(url);
        this.additionalHeaders.put("API-Key", apiKey);
    }

    public <T> T get(String endpoint, Class<T> tClass) throws IOException {
        String fullUrl = baseUrl + endpoint;
        return getFullUrl(additionalHeaders, fullUrl, tClass);
    }

    public <T> T get(String endpoint, HashMap<String, Object> query, Class<T> tClass) throws IOException {
        String base = baseUrl + endpoint;
        String queryStr = parseQueries(query);
        String fullUrl = (queryStr.isEmpty()) ? base : base + "?" + queryStr;
        return getFullUrl(additionalHeaders, fullUrl, tClass);
    }

    public <T> T post(String endpoint, T body, Class<T> tClass) throws IOException {
        String fullUrl = baseUrl + endpoint;
        return postFullUrl(additionalHeaders, fullUrl, body, tClass);
    }

    public <T> T post(String endpoint, HashMap<String, Object> query, T body, Class<T> tClass) throws IOException {
        String base = baseUrl + endpoint;
        String queryStr = parseQueries(query);
        String fullUrl = (queryStr.isEmpty()) ? base : base + "?" + queryStr;
        return postFullUrl(additionalHeaders, fullUrl, body, tClass);
    }

    public static String getRaw(Map<String, String> headers, String url) throws IOException {
        HttpURLConnection connection = (HttpURLConnection) new URL(url).openConnection();
        addHeaders(connection, headers);
        connection.setRequestMethod("GET");
        int responseCode = connection.getResponseCode();
        BufferedReader in;
        if (responseCode >= 200 && responseCode < 400) {
            in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        } else {
            in = new BufferedReader(new InputStreamReader(connection.getErrorStream()));
        }
        String inputLine;
        StringBuilder response = new StringBuilder();
        while ((inputLine = in.readLine()) != null) {
            response.append(inputLine);
        }
        in.close();
        return response.toString();
    }

    public static String postRaw(Map<String, String> headers, String url, String body) throws IOException {
        HttpURLConnection connection = (HttpURLConnection) new URL(url).openConnection();
        addHeaders(connection, headers);
        connection.setRequestMethod("POST");
        connection.setDoOutput(true);
        try (OutputStream os = connection.getOutputStream()) {
            byte[] input = body.getBytes("utf-8");
            os.write(input, 0, input.length);
        }
        int responseCode = connection.getResponseCode();
        BufferedReader in;
        if (responseCode >= 200 && responseCode < 400) {
            in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        } else {
            in = new BufferedReader(new InputStreamReader(connection.getErrorStream()));
        }
        String inputLine;
        StringBuilder response = new StringBuilder();
        while ((inputLine = in.readLine()) != null) {
            response.append(inputLine);
        }
        in.close();
        return response.toString();
    }

    public static void addHeaders(HttpURLConnection connection, Map<String, String> headers) {
        for (Map.Entry<String, String> entry : headers.entrySet()) {
            connection.setRequestProperty(entry.getKey(), entry.getValue());
        }
    }

    public static String parseQueries(HashMap<String, Object> query) {
        return query.entrySet().stream()
                .map(e -> urlEncodeUTF8(e.getKey()) + "=" + urlEncodeUTF8(e.getValue().toString()))
                .collect(Collectors.joining("&"));
    }

    public static <T> T getFullUrl(Map<String, String> headers, String url, Class<T> tClass) throws IOException {
        String response = getRaw(headers, url);
        return new Gson().fromJson(response, tClass);
    }

    public static <T> T getFullUrl(Map<String, String> headers, String url, HashMap<String, Object> query, Class<T> tClass) throws IOException {
        String queryStr = parseQueries(query);
        String fullUrl = (queryStr.isEmpty()) ? url : url + "?" + queryStr;
        return getFullUrl(headers, fullUrl, tClass);
    }

    public static <T> T postFullUrl(Map<String, String> headers, String url, T body, Class<T> tClass) throws IOException {
        String jsonBody = new Gson().toJson(body);
        String response = postRaw(headers, url, jsonBody);
        return new Gson().fromJson(response, tClass);
    }

    public static <T> T postFullUrl(Map<String, String> headers, String url, HashMap<String, Object> query, T body, Class<T> tClass) throws IOException {
        String queryStr = parseQueries(query);
        String fullUrl = (queryStr.isEmpty()) ? url : url + "?" + queryStr;
        return postFullUrl(headers, fullUrl, body, tClass);
    }

    public static String urlEncodeUTF8(String input) {
        try {
            return java.net.URLEncoder.encode(input, "UTF-8");
        } catch (Exception e) {
            return input;
        }
    }
}
