import android.app.DownloadManager;
import android.net.Uri;
import android.os.Environment;
import android.webkit.MimeTypeMap;
import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.URL;
import io.reactivex.Single;

public class FetchUrlMimeType {
    private final DownloadManager mDownloadManager;
    private final DownloadManager.Request mRequest;
    private final String mUri;
    private final String mCookies;
    private final String mUserAgent;

    public FetchUrlMimeType(DownloadManager downloadManager, DownloadManager.Request request, String uri, String cookies, String userAgent) {
        mDownloadManager = downloadManager;
        mRequest = request;
        mUri = uri;
        mCookies = cookies;
        mUserAgent = userAgent;
    }

    public Single<Result> create() {
        return Single.create(emitter -> {
            try {
                URL url = new URL(mUri);
                HttpURLConnection connection = (HttpURLConnection) url.openConnection();
                connection.setRequestProperty("User-Agent", mUserAgent);
                if (mCookies != null) {
                    connection.setRequestProperty("Cookie", mCookies);
                }
                connection.connect();

                String mimeType = connection.getContentType();
                if (mimeType == null) {
                    mimeType = "application/octet-stream";
                }

                String contentDisposition = connection.getHeaderField("Content-Disposition");
                String filename = extractFilenameFromContentDisposition(contentDisposition);
                if (filename == null) {
                    filename = extractFilenameFromUrl(url.toString());
                }

                File destinationDir = Environment.getExternalStoragePublicDirectory(Environment.DIRECTORY_DOWNLOADS);
                if (destinationDir == null || !destinationDir.exists()) {
                    emitter.onSuccess(Result.FAILURE_LOCATION);
                    return;
                }

                mRequest.setMimeType(mimeType);
                mRequest.setDestinationInExternalPublicDir(Environment.DIRECTORY_DOWNLOADS, filename);

                long id = mDownloadManager.enqueue(mRequest);
                if (id == 0) {
                    emitter.onSuccess(Result.FAILURE_ENQUEUE);
                } else {
                    emitter.onSuccess(Result.SUCCESS);
                }
            } catch (Exception e) {
                emitter.onSuccess(Result.FAILURE_ENQUEUE);
            }
        });
    }

    private String extractFilenameFromContentDisposition(String contentDisposition) {
        if (contentDisposition == null) {
            return null;
        }
        String[] parts = contentDisposition.split(";");
        for (String part : parts) {
            part = part.trim();
            if (part.startsWith("filename=")) {
                String filename = part.substring("filename=".length());
                if (filename.length() > 0) {
                    if (filename.charAt(0) == '"') {
                        filename = filename.substring(1, filename.length() - 1);
                    }
                    return filename;
                }
            }
        }
        return null;
    }

    private String extractFilenameFromUrl(String url) {
        String[] pathParts = url.split("/");
        String filename = pathParts[pathParts.length - 1];
        if (filename.isEmpty()) {
            filename = "downloaded_file";
        }
        return filename;
    }

    public enum Result {
        FAILURE_ENQUEUE, FAILURE_LOCATION, SUCCESS
    }
}
