import java.io.IOException;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.lang.foreign.Arena;
import java.lang.foreign.MemorySegment;
import java.nio.channels.FileChannel;

public class MemorySegmentReaderSupplier {
    private final Arena arena;
    private final MemorySegment memory;

    public MemorySegmentReaderSupplier(Path path) throws IOException {
        try (FileChannel channel = FileChannel.open(path, StandardOpenOption.READ)) {
            arena = Arena.ofConfined();
            memory = channel.mapToMemory(arena);
        }
    }

    public MemorySegmentReader get() {
        return new MemorySegmentReader(memory);
    }

    public void close() {
        if (arena != null) {
            arena.close();
        }
    }

    public static class MemorySegmentReader {
        private final MemorySegment segment;

        public MemorySegmentReader(MemorySegment segment) {
            this.segment = segment;
        }

        public byte getByte(long index) {
            return segment.get(ValueLayout.JAVA_BYTE, index);
        }

        public char getChar(long index) {
            return segment.get(ValueLayout.JAVA_CHAR, index);
        }

        public short getShort(long index) {
            return segment.get(ValueLayout.JAVA_SHORT, index);
        }

        public int getInt(long index) {
            return segment.get(ValueLayout.JAVA_INT, index);
        }

        public long getLong(long index) {
            return segment.get(ValueLayout.JAVA_LONG, index);
        }

        public float getFloat(long index) {
            return segment.get(ValueLayout.JAVA_FLOAT, index);
        }

        public double getDouble(long index) {
            return segment.get(ValueLayout.JAVA_DOUBLE, index);
        }

        public String getString(long index, int length) {
            return segment.getString(index, length);
        }

        public long size() {
            return segment.byteSize();
