                logger.info("Successfully connected to proxy server.");
            } else {
                logger.error("Failed to connect to proxy server: {}", future.cause().getMessage());
                reconnectWait();
                connectProxyServer();
            }
        });
    }

    public void stop() {
        logger.info("Stopping ProxyClientContainer...");
        workerGroup.shutdownGracefully();
    }

    public void channelInactive(ChannelHandlerContext ctx) {
        logger.warn("Connection to proxy server lost. Attempting to reconnect...");
        reconnectWait();
        connectProxyServer();
    }

    public void reconnectWait() {
        try {
            TimeUnit.MILLISECONDS.sleep(sleepTimeMill);
            sleepTimeMill *= 2;
            if (sleepTimeMill > 32000) {
                sleepTimeMill = 32000;
            }
        } catch (InterruptedException e) {
            logger.error("Reconnection wait interrupted: {}", e.getMessage());
            Thread.currentThread().interrupt();
        }
    }
}

