            reconnect();
        }
    }

    public void reconnect() {
        logger.info("Reconnecting to server in {} seconds...", RECONNECT_SECONDS);
        eventGroup.schedule(() -> {
            if (channel != null && channel.isActive()) {
                logger.info("Channel is already active, no need to reconnect.");
                return;
