            start();
        }, RECONNECT_SECONDS, TimeUnit.SECONDS);
    }

    public void send(Invocation invocation) {
        if (channel == null || !channel.isActive()) {
            logger.warn("Cannot send message; channel is not active. Attempting to reconnect.");
            reconnect();
            return;
        }
        channel.writeAndFlush(invocation);
    }

    @PreDestroy
    public void shutdown() {
        if (channel != null) {
            channel.closeFuture().addListener(future -> {
                if (future.isSuccess()) {
                    logger.info("Channel closed gracefully.");
                } else {
                    logger.error("Failed to close channel.", future.cause());
                }
            });
        }
        eventGroup.shutdownGracefully();
        logger.info("Netty client shutdown completed.");
    }

    private class ClientHandler extends SimpleChannelInboundHandler<Invocation> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Invocation msg) throws Exception {
            logger.info("Received message from server: {}", msg);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            logger.error("Exception caught in client handler:", cause);
            ctx.close();
            reconnect();
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            logger.info("Client connected to server.");
        }

        @Override
        public void channelInactive(ChannelHandlerContext ctx) {
            logger.warn("Client disconnected from server. Attempting to reconnect.");
