            Channel channel = bootstrap.connect(host, 8080).sync().channel();
            channelMap.put(host, channel);
            channel.writeAndFlush(message);
            return channel;
        } catch (Exception e) {
            group.shutdownGracefully();
            groupMap.remove(host);
            throw new RuntimeException("Failed to connect to " + host, e);
