import io.netty.util.concurrent.Future;

import java.net.URI;
import java.net.URISyntaxException;

public class WebsocketProxy implements Proxy {
    @Override
    public void connect(String serverAddress, int port) throws InterruptedException, URISyntaxException {
        URI uri = new URI("ws://" + serverAddress + ":" + port);
        WebSocketClientHandshaker handshaker = DefaultWebSocketClientHandshaker.create(
                uri, WebSocketVersion.V13, null, false, 65536);

        EventLoopGroup group = new NioEventLoopGroup();
        try {
            Bootstrap bootstrap = new Bootstrap();
            bootstrap.group(group)
                    .channel(NioSocketChannel.class)
                    .handler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) {
                            ChannelPipeline pipeline = ch.pipeline();
                            pipeline.addLast(new HttpClientCodec());
                            pipeline.addLast(new HttpObjectAggregator(65536));
                            pipeline.addLast(new IdleStateHandler(0, 0, 60));
                            pipeline.addLast(new WebSocketClientHandler(handshaker));
                        }
                    });

            Channel channel = bootstrap.connect(uri.getHost(), uri.getPort()).sync().channel();
            handshaker.handshake(channel).await();

            // Wait until the connection is closed.
            channel.closeFuture().sync();
        } finally {
            group.shutdownGracefully();
        }
    }

    private static class WebSocketClientHandler extends SimpleChannelInboundHandler<Object> {
        private final WebSocketClientHandshaker handshaker;

        public WebSocketClientHandler(WebSocketClientHandshaker handshaker) {
            this.handshaker = handshaker;
        }

        @Override
        public void handlerAdded(ChannelHandlerContext ctx) {
            handshaker.handshake(ctx.channel());
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Object msg) {
            if (!handshaker.isHandshakeComplete()) {
                handshaker.finishHandshake(ctx.channel(), (HttpResponse) msg);
                return;
            }
            if (msg instanceof TextWebSocketFrame) {
                TextWebSocketFrame frame = (TextWebSocketFrame) msg;
                System.out.println("Received: " + frame.text());
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }
}

