import java.net.InetSocketAddress;
import java.util.concurrent.TimeUnit;

public abstract class TrackerClient {
    protected boolean secure;
    protected long interval;
    protected Bootstrap bootstrap;
    protected int port;
    protected String address;
    protected String[] devices;
    protected ChannelGroup channelGroup;

    public TrackerClient(Config config, String protocol) {
        this.secure = config.isSecure();
        this.interval = config.getReconnectInterval();
        this.port = config.getPort();
        this.address = config.getAddress();
        this.devices = config.getDevices();
        this.channelGroup = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);
        initializeBootstrap();
    }

    protected void initializeBootstrap() {
        bootstrap = new Bootstrap();
        EventLoopGroup group = new NioEventLoopGroup();
        bootstrap.group(group)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        addProtocolHandlers(new PipelineBuilder(), new Config());
                        if (secure) {
                            SslContext sslCtx = SslContextBuilder.forClient()
                                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                                    .build();
                            ch.pipeline().addFirst(sslCtx.newHandler(ch.alloc()));
                        }
                    }
                });
    }

    protected abstract void addProtocolHandlers(PipelineBuilder pipeline, Config config);

    public void start() {
        connect();
        if (interval > 0) {
            scheduleReconnect();
        }
    }

    private void connect() {
        ChannelFuture future = bootstrap.connect(new InetSocketAddress(address, port));
        future.addListener((ChannelFutureListener) future1 -> {
            if (future1.isSuccess()) {
                Channel channel = future1.channel();
                channelGroup.add(channel);
                System.out.println("Connected to server: " + address + ":" + port);
            } else {
                System.err.println("Failed to connect to server: " + address + ":" + port);
                future1.cause().printStackTrace();
                if (interval > 0) {
                    scheduleReconnect();
                }
            }
        });
    }

    private void scheduleReconnect() {
        ScheduledExecutorService scheduler = Executors.newScheduledThreadPool(1);
        scheduler.scheduleAtFixedRate(this::connect, interval, interval, TimeUnit.SECONDS);
    }

    public void stop() {
        channelGroup.close().awaitUninterruptibly();
        bootstrap.config().group().shutdownGracefully();
    }

    public boolean isDatagram() {
        return false;
    }

    public boolean isSecure() {
        return secure;
    }

    public String[] getDevices() {
        return devices;
    }

    public ChannelGroup getChannelGroup() {
        return channelGroup;
    }
}

