public class NettyTcpClient {
    private final Bootstrap bootstrap;
    private final EventLoopGroup eventLoopGroup;
    private final int connectTimeout;
    private final int readTimeout;
    private final String threadPrefix;

    public NettyTcpClient(int connectTimeout, int readTimeout, String threadPrefix) {
        this.connectTimeout = connectTimeout;
        this.readTimeout = readTimeout;
        this.threadPrefix = threadPrefix;
        this.eventLoopGroup = new NioEventLoopGroup(1, new DefaultThreadFactory(threadPrefix));
        this.bootstrap = new Bootstrap();
        bootstrap.group(eventLoopGroup)
                .channel(io.netty.channel.socket.nio.NioSocketChannel.class)
                .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, connectTimeout);
    }

    public void init(DockerClientConfig dockerClientConfig) {
        bootstrap.handler(new ChannelInitializer<Channel>() {
            @Override
            protected void initChannel(Channel ch) throws Exception {
                configure(ch);
            }
        });
    }

    public <T extends Channel> T connect(DockerClientConfig dockerClientConfig) throws Exception {
        SslHandler sslHandler = initSsl(dockerClientConfig);
        bootstrap.handler(new ChannelInitializer<Channel>() {
            @Override
            protected void initChannel(Channel ch) throws Exception {
                configure(ch);
                ch.pipeline().addLast(sslHandler);
            }
        });

        SocketAddress remoteAddress = new InetSocketAddress(dockerClientConfig.getHost(), dockerClientConfig.getPort());
        return (T) bootstrap.connect(remoteAddress).sync().channel();
    }

    public SslHandler initSsl(DockerClientConfig dockerClientConfig) {
        SslContext sslContext = SslContextBuilder.forClient()
                .trustManager(dockerClientConfig.getTrustStore())
                .keyManager(dockerClientConfig.getKeyStore(), dockerClientConfig.getKeyStorePassword())
                .build();

        SslHandler sslHandler = sslContext.newHandler(null);
        SSLParameters sslParameters = sslHandler.engine().getSSLParameters();
        enableHostNameVerification(sslParameters);
        sslHandler.engine().setSSLParameters(sslParameters);
        return sslHandler;
    }

    public SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setNeedClientAuth(false);
        sslParameters.setWantClientAuth(false);
        sslParameters.setUseServerNameIndication(true);
        return sslParameters;
    }

    public void close() {
        eventLoopGroup.shutdownGracefully(100, 1000, TimeUnit.MILLISECONDS);
    }

    public <T extends Channel> T configure(T channel) {
        channel.config().setOption(ChannelOption.SO_KEEPALIVE, true);
        channel.pipeline().addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
        return channel;
    }

    public class ReadTimeoutHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) {
            if (evt instanceof ReadTimeoutException) {
                ctx.close();
            }
        }
    }
