        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    public class LocalServerWebsocketEventImp extends SimpleChannelInboundHandler<WebSocketFrame> {
        private static final AttributeKey<Protocol> PROTOCOL_ATTR = AttributeKey.valueOf("protocol");

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, WebSocketFrame frame) throws Exception {
            if (frame instanceof TextWebSocketFrame) {
                onTextMessage(ctx, ((TextWebSocketFrame) frame).text());
            } else if (frame instanceof BinaryWebSocketFrame) {
                onBinaryMessage(ctx, ((BinaryWebSocketFrame) frame).content().array());
            } else if (frame instanceof CloseWebSocketFrame) {
                onDisconnect(ctx);
            }
        }

        @Override
        public void handlerAdded(ChannelHandlerContext ctx) throws Exception {
            onConnect(ctx);
        }

        @Override
        public void handlerRemoved(ChannelHandlerContext ctx) throws Exception {
            onDisconnect(ctx);
        }

        public void onConnect(ChannelHandlerContext ctx) {
            logger.info("Client connected: {}", ctx.channel().remoteAddress());
            Protocol protocol = new Protocol();
            ctx.channel().attr(PROTOCOL_ATTR).set(protocol);
            protocolList.add(protocol);
        }

        public void onDisconnect(ChannelHandlerContext ctx) {
            logger.info("Client disconnected: {}", ctx.channel().remoteAddress());
            Protocol protocol = ctx.channel().attr(PROTOCOL_ATTR).get();
            if (protocol != null) {
                protocolList.remove(protocol);
                protocol.close();
            }
            ctx.close();
        }

        public void onTextMessage(ChannelHandlerContext ctx, String text) {
            try {
                Command command = Command.fromString(text);
                if (command != null) {
                    executeCommand(ctx, command);
                } else {
                    logger.warn("Received unknown command: {}", text);
                }
            } catch (Exception e) {
                logger.error("Error processing text message: {}", text, e);
            }
        }

        public void onBinaryMessage(ChannelHandlerContext ctx, byte[] data) {
            try {
                Protocol protocol = ctx.channel().attr(PROTOCOL_ATTR).get();
                if (protocol != null) {
                    protocol.handleBinaryData(data);
                } else {
                    logger.warn("Received binary data but no protocol is associated with the channel.");
                }
            } catch (Exception e) {
                logger.error("Error processing binary message", e);
            }
        }

        public void initLocalClient(ChannelHandlerContext ctx, Command command) {
            Protocol protocol = new Protocol();
            ctx.channel().attr(PROTOCOL_ATTR).set(protocol);
            protocolList.add(protocol);
            executeCommand(ctx, command);
        }

        public void executeCommand(ChannelHandlerContext ctx, Command command) {
            Protocol protocol = ctx.channel().attr(PROTOCOL_ATTR).get();
            if (protocol != null) {
                protocol.execute(command);
            } else {
                logger.warn("No protocol found for executing command: {}", command);
            }
        }
    }

    public static class Protocol {
        private boolean isOpen = true;

        public void close() {
            isOpen = false;
        }

        public void handleBinaryData(byte[] data) {
            // Handle binary data
        }

        public void execute(Command command) {
            // Execute command logic
        }
    }

    public static class Command {
        private String name;
        private String payload;

        public Command(String name, String payload) {
            this.name = name;
            this.payload = payload;
        }

        public static Command fromString(String input) {
            // Parse command from string
            return new Command("example", input);
        }

        @Override
        public String toString() {
            return "Command{" +
                    "name='" + name + '\'' +
                    ", payload='" + payload + '\'' +
                    '}';
        }
    }
}

