public class HttpStaticFileServer {
    private final boolean SSL;
    private final int PORT;
    private final String DOCUMENT_ROOT;

    public HttpStaticFileServer(boolean ssl, int port, String documentRoot) {
        this.SSL = ssl;
        this.PORT = port;
        this.DOCUMENT_ROOT = documentRoot;
    }

    public void start() throws Exception {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) {
                     if (SSL) {
                         ch.pipeline().addLast("ssl", getSSLContext().newEngine(ch.alloc()));
                     }
                     ch.pipeline().addLast(new HttpRequestDecoder());
                     ch.pipeline().addLast(new HttpObjectAggregator(65536));
                     ch.pipeline().addLast(new HttpResponseEncoder());
                     ch.pipeline().addLast(new StaticFileHandler(DOCUMENT_ROOT));
