    public static final int DEFAULT_ACCEPT_THREAD_COUNT = 1;
    public static final int DEFAULT_IO_THREAD_COUNT = 4;

    @PostConstruct
    public void startServer() {
        int acceptThreadCount = environment.get("netty.accept.threads", DEFAULT_ACCEPT_THREAD_COUNT);
        int ioThreadCount = environment.get("netty.io.threads", DEFAULT_IO_THREAD_COUNT);
        boolean sslEnabled = environment.getBoolean("netty.ssl.enabled", false);
        String keyStorePath = environment.get("netty.ssl.key-store-path", "server-keystore.jks");
        String keyStorePassword = environment.get("netty.ssl.key-store-password", "password");

        bossGroup = new NioEventLoopGroup(acceptThreadCount);
