        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<Channel>() {
                        @Override
                        protected void initChannel(Channel ch) throws Exception {
                            if (sslEnabled) {
                                SslContext sslContext = SslContextBuilder.forServer(new File(keyStorePath), keyStorePassword, keyStorePassword).build();
                                ch.pipeline().addLast(sslContext.newHandler(ch.alloc()));
                            }
                            // Add your custom handlers here
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            channel = bootstrap.bind(new InetSocketAddress(environment.get("netty.port", 8080))).sync().channel();
            System.out.println("Netty server started on port " + environment.get("netty.port", 8080));
        } catch (Exception e) {
            System.err.println("Failed to start Netty server: " + e.getMessage());
            stop();
        }
    }

    @PreDestroy
    public void stop() {
