            channel.closeFuture().addListener((Future<? super Void> future) -> {
                if (future.isSuccess()) {
                    System.out.println("Netty server closed successfully.");
                } else {
                    System.err.println("Netty server failed to close: " + future.cause().getMessage());
                }
            });
        }

        if (bossGroup != null) {
            bossGroup.shutdownGracefully(0, 10, TimeUnit.SECONDS);
        }

        if (workerGroup != null) {
            workerGroup.shutdownGracefully(0, 10, TimeUnit.SECONDS);
        }
    }
}

