        this.workerGroup = new NioEventLoopGroup();
        this.threadPoolExecutor = createThreadPoolExecutor();
        this.rejectCounter = new AtomicInteger(0);
        this.logger = LoggerFactory.getLogger(NettyServer.class);
        this.state = ChannelState.CLOSED;
        this.codec = new Codec();
    }

    private ThreadPoolExecutor createThreadPoolExecutor() {
        int corePoolSize = 10;
        int maximumPoolSize = 20;
        long keepAliveTime = 60L;
        return new ThreadPoolExecutor(corePoolSize, maximumPoolSize, keepAliveTime, TimeUnit.SECONDS,
                new LinkedBlockingQueue<>(100), new ThreadPoolExecutor.CallerRunsPolicy());
    }

    public boolean open() {
        if (state != ChannelState.CLOSED) {
            logger.warn("Server is already in state: " + state);
            return false;
        }
        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) {
                            ChannelPipeline pipeline = ch.pipeline();
                            pipeline.addLast(codec);
                            pipeline.addLast(new ServerHandler(messageHandler, threadPoolExecutor, rejectCounter, channelManage, logger));
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            serverChannel = bootstrap.bind(url.getPort()).sync().channel();
            state = ChannelState.OPEN;
            logger.info("Server started on port: " + url.getPort());
            return true;
        } catch (Exception e) {
            logger.error("Failed to start server", e);
            cleanup();
            return false;
        }
    }

    public void close(int timeout) {
        if (state == ChannelState.CLOSED) {
            return;
        }
        try {
            if (serverChannel != null) {
                serverChannel.close().await(timeout);
            }
            bossGroup.shutdownGracefully().await(timeout);
            workerGroup.shutdownGracefully().await(timeout);
            cleanup();
            state = ChannelState.CLOSED;
            logger.info("Server closed gracefully");
        } catch (InterruptedException e) {
            logger.error("Interrupted during server shutdown", e);
            Thread.currentThread().interrupt();
        }
    }

    private void cleanup() {
        if (serverChannel != null) {
            serverChannel.closeNow();
            serverChannel = null;
        }
        channelManage.clear();
        logger.info("Server resources cleaned up");
    }

    public enum ChannelState {
