import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import io.netty.util.CharsetUtil;

import java.lang.annotation.ElementType;
import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;
import java.lang.reflect.Method;
import java.util.*;
import java.util.logging.Logger;

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@interface RequestMapping {
    String value();
}

class DefaultController {
    public FullHttpResponse handleRequest(String uri, Channel channel, FullHttpRequest request) {
        return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.NOT_FOUND,
                Unpooled.copiedBuffer("404 Not Found", CharsetUtil.UTF_8));
    }
}

public class EmbedHttpServer {
    private final int port;
    private final DefaultController defaultController;
    private final List<Object> controllerList;
    private static final Logger LOGGER = Logger.getLogger(EmbedHttpServer.class.getName());

    public EmbedHttpServer(int port) {
        this.port = port;
        this.defaultController = new DefaultController();
        this.controllerList = new ArrayList<>();
    }

    public EmbedHttpServer addController(Object obj) {
        controllerList.add(obj);
        return this;
    }

    private String fixUri(String uri) {
        if (uri == null || uri.isEmpty()) {
            return "/";
        }
        if (uri.charAt(0) != '/') {
            uri = "/" + uri;
        }
        if (uri.charAt(uri.length() - 1) == '/') {
            uri = uri.substring(0, uri.length() - 1);
        }
        return uri;
    }

    private FullHttpResponse invoke(String uri, Channel channel, FullHttpRequest request) {
        uri = fixUri(uri);
        Method methodToInvoke = null;
        Object controllerInstance = null;

        for (Object controller : controllerList) {
            for (Method method : controller.getClass().getDeclaredMethods()) {
                RequestMapping mapping = method.getAnnotation(RequestMapping.class);
                if (mapping != null && mapping.value().equals(uri)) {
                    methodToInvoke = method;
                    controllerInstance = controller;
                    break;
                }
            }
            if (methodToInvoke != null) {
                break;
            }
        }

        if (methodToInvoke != null) {
            try {
                methodToInvoke.setAccessible(true);
                Object result = methodToInvoke.invoke(controllerInstance, uri, channel, request);
                if (result instanceof FullHttpResponse) {
                    return (FullHttpResponse) result;
                } else {
                    return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK,
                            Unpooled.copiedBuffer(result.toString(), CharsetUtil.UTF_8));
                }
            } catch (Exception e) {
                LOGGER.severe("Error invoking controller method: " + e.getMessage());
                return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.INTERNAL_SERVER_ERROR,
                        Unpooled.copiedBuffer("500 Internal Server Error", CharsetUtil.UTF_8));
            }
        } else {
            return defaultController.handleRequest(uri, channel, request);
        }
    }

    public void start(GenericFutureListener<? extends Future<? super Void>> startedListener) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .handler(new LoggingHandler(LogLevel.INFO))
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 public void initChannel(SocketChannel ch) {
                     ch.pipeline().addLast(new HttpServerCodec());
                     ch.pipeline().addLast(new HttpObjectAggregator(65536));
                     ch.pipeline().addLast(new EmbedHttpServerHandler());
                 }
             });

            ChannelFuture f = b.bind(port).addListener(startedListener);
            f.sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private class EmbedHttpServerHandler extends SimpleChannelInboundHandler<FullHttpRequest> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest request) throws Exception {
            try {
                FullHttpResponse response = invoke(request.uri(), ctx.channel(), request);
                response.headers().set(HttpHeaderNames.CONTENT_LENGTH, response.content().readableBytes());
                ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
            } catch (Exception e) {
                LOGGER.severe("Error processing request: " + e.getMessage());
                FullHttpResponse errorResponse = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1,
                        HttpResponseStatus.INTERNAL_SERVER_ERROR,
                        Unpooled.copiedBuffer("500 Internal Server Error", CharsetUtil.UTF_8));
                errorResponse.headers().set(HttpHeaderNames.CONTENT_LENGTH, errorResponse.content().readableBytes());
                ctx.writeAndFlush(errorResponse).addListener(ChannelFutureListener.CLOSE);
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            LOGGER.severe("Unexpected exception from downstream: " + cause.getMessage());
            ctx.close();
        }
    }
}

