    private static final String KEYSTORE_PATH = "path/to/keystore.jks";
    private static final String KEYSTORE_PASSWORD = "password";

    public void start() throws Exception {
        // Configure SSL context if enabled
        SslContext sslCtx = null;
        if (SSL) {
            try {
                File keystoreFile = new File(KEYSTORE_PATH);
                if (!keystoreFile.exists()) {
                    throw new IOException("Keystore file not found: " + KEYSTORE_PATH);
                }
                sslCtx = SslContextBuilder.forClient()
                        .keyStore(keystoreFile, KEYSTORE_PASSWORD)
                        .build();
            } catch (SSLException | IOException e) {
                System.err.println("Failed to initialize SSL context: " + e.getMessage());
                throw e;
            }
        }

        // Create boss and worker groups
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

