public abstract class AbstractHttp2ServerTransport {
    protected final Logger LOGGER = LoggerFactory.getLogger(AbstractHttp2ServerTransport.class);
    protected ServerBootstrap serverBootstrap;
    protected EventLoopGroup bizGroup;
    protected ServerTransportConfig transportConfig;

    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
    }

    public boolean start() {
        try {
            bizGroup = new NioEventLoopGroup();
            serverBootstrap = new ServerBootstrap();
            serverBootstrap.group(bizGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) {
                            configurePipeline(ch);
                        }
                    })
                    .option(io.netty.channel.ChannelOption.SO_BACKLOG, transportConfig.getSoBacklog())
                    .childOption(io.netty.channel.ChannelOption.SO_KEEPALIVE, transportConfig.isSoKeepalive())
                    .childOption(io.netty.channel.ChannelOption.TCP_NODELAY, transportConfig.isTcpNoDelay());

            if (transportConfig.isSslEnabled()) {
                SslContext sslCtx = SslContextBuilder.forServer(new SelfSignedCertificate().certificate(), new SelfSignedCertificate().privateKey()).build();
                serverBootstrap.childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ch.pipeline().addLast(sslCtx.newHandler(ch.alloc()));
                        configurePipeline(ch);
                    }
                });
            }

            ChannelFuture future = serverBootstrap.bind(transportConfig.getHost(), transportConfig.getPort()).sync();
            future.addListener(future1 -> {
                if (future1.isSuccess()) {
                    LOGGER.info("Server started on {}:{}", transportConfig.getHost(), transportConfig.getPort());
                } else {
                    LOGGER.error("Failed to start server", future1.cause());
                }
            });

            return true;
        } catch (Exception e) {
            LOGGER.error("Error starting server", e);
            return false;
        }
    }

    protected abstract void configurePipeline(SocketChannel ch);

    public void stop() {
        if (bizGroup != null) {
            bizGroup.shutdownGracefully();
        }
        LOGGER.info("Server stopped");
    }
}

