import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.StandardOpenOption;
import java.util.Arrays;

public class FileChannelInteractor {
    private final FileChannel fileChannel;
    private final long mapAddress;
    private final long fileSize;

    public FileChannelInteractor(String filePath) throws IOException {
        File file = new File(filePath);
        this.fileChannel = FileChannel.open(file.toPath(), StandardOpenOption.READ);
        this.fileSize = file.length();
        MappedByteBuffer mappedByteBuffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, fileSize);
        this.mapAddress = ((sun.nio.ch.DirectBuffer) mappedByteBuffer).address();
    }

    public long getMemoryMappedAddress() {
        return mapAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

    public void close() throws IOException {
        if (fileChannel != null) {
            fileChannel.close();
        }
    }

    public static void spawnWorkerProcess() {
        String[] command = new String[Arrays.asList(System.getProperty("sun.java.command").split(" ")).size() + 1];
        String[] args = System.getProperty("sun.java.command").split(" ");
        for (int i = 0; i < args.length; i++) {
            command[i] = args[i];
        }
        command[args.length] = "--worker";
        try {
            ProcessBuilder processBuilder = new ProcessBuilder(command);
            processBuilder.inheritIO();
            Process process = processBuilder.start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

