import java.util.Map;
import java.util.Random;

public class Console {
    private static final Console instance = new Console();
    private final Map<String, Class<? extends Command>> commands = new HashMap<>();

    private Console() {
        registerCommands();
    }

    public static Console getInstance() {
        return instance;
    }

    private void registerCommands() {
        commands.put("help", HelpCommand.class);
        commands.put("hello", HelloCommand.class);
        commands.put("device", DeviceCommand.class);
    }

    public void listenOnTCP(int port) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        public void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(new StringDecoder());
                            ch.pipeline().addLast(new StringEncoder());
                            ch.pipeline().addLast(instance.new Adapter());
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private class Adapter extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            String command = (String) msg;
            Command cmd = null;
            try {
                if (commands.containsKey(command)) {
                    cmd = commands.get(command).getDeclaredConstructor(String.class).newInstance(command);
                    String response = cmd.execute();
                    sendString(ctx, response);
                } else {
                    sendString(ctx, "Unknown command: " + command + "\n");
                }
            } catch (Exception e) {
                sendString(ctx, "Error processing command: " + e.getMessage() + "\n");
            } finally {
                sendPrompt(ctx);
            }
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            sendPrompt(ctx);
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) {
            ctx.flush();
        }

        private void sendStringL(ChannelHandlerContext ctx, String text) {
            if (!text.endsWith("\n")) {
                text += "\n";
            }
            sendString(ctx, text);
        }

        private void sendString(ChannelHandlerContext ctx, String text) {
            ByteBuf buf = Unpooled.copiedBuffer(text, CharsetUtil.UTF_8);
            ctx.write(buf);
        }

        private void sendPrompt(ChannelHandlerContext ctx) {
            sendStringL(ctx, "Enter command: ");
        }
    }

    public abstract static class Command {
        protected final String command;

        protected Command(String command) {
            this.command = command;
        }

        public abstract String execute();
    }

    public static class HelpCommand extends Command {
