            super(command);
        }

        @Override
        public String execute() {
            StringBuilder sb = new StringBuilder();
            sb.append("Available commands:\n");
            sb.append("  help - Show this help message\n");
            sb.append("  hello - Get a random greeting\n");
            sb.append("  device - Get device information in JSON format\n");
            return sb.toString();
        }
    }

    public static class HelloCommand extends Command {
