import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioServerSocketChannel;
import io.netty.channel.socket.SocketChannel;
import io.netty.handler.timeout.IdleState;
import io.netty.handler.timeout.IdleStateHandler;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class NettyRemotingServer {
    private final ServerBootstrap serverBootstrap;
    private final EventLoopGroup bossSelectorGroup;
    private final EventLoopGroup workerSelectorGroup;
    private final DefaultEventExecutorGroup defaultEventExecutorGroup;
    private final AppContext appContext;
    private final Logger LOGGER = LoggerFactory.getLogger(NettyRemotingServer.class);
    private final ChannelEventListener channelEventListener;

    public NettyRemotingServer(AppContext appContext, RemotingServerConfig config) {
        this(appContext, config, null);
    }

    public NettyRemotingServer(AppContext appContext, RemotingServerConfig config, ChannelEventListener channelEventListener) {
        this.appContext = appContext;
        this.channelEventListener = channelEventListener;
        this.bossSelectorGroup = new NioEventLoopGroup(config.getBossThreadSize());
        this.workerSelectorGroup = new NioEventLoopGroup(config.getWorkerThreadSize());
        this.defaultEventExecutorGroup = new DefaultEventExecutorGroup(config.getBusinessThreadSize());
        this.serverBootstrap = new ServerBootstrap();
    }

    public void serverStart() {
        try {
            serverBootstrap.group(bossSelectorGroup, workerSelectorGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(defaultEventExecutorGroup, new NettyConnectManageHandler(channelEventListener));
                            ch.pipeline().addLast(defaultEventExecutorGroup, new IdleStateHandler(0, 0, config.getReadIdleSeconds(), TimeUnit.SECONDS));
                            ch.pipeline().addLast(defaultEventExecutorGroup, new NettyServerHandler(appContext));
                        }
                    });

            String bindAddress = "0.0.0.0:" + config.getPort();
            ChannelFuture future = serverBootstrap.bind(bindAddress).sync();
            LOGGER.info("Netty server started and bound to {}", bindAddress);
            future.channel().closeFuture().sync();
        } catch (Exception e) {
            LOGGER.error("Failed to start Netty server", e);
        } finally {
            serverShutdown();
        }
    }

    public void serverShutdown() {
        if (bossSelectorGroup != null) {
            bossSelectorGroup.shutdownGracefully();
        }
        if (workerSelectorGroup != null) {
            workerSelectorGroup.shutdownGracefully();
        }
        if (defaultEventExecutorGroup != null) {
            defaultEventExecutorGroup.shutdownGracefully();
        }
        LOGGER.info("Netty server shutdown completed");
    }

    public class NettyServerHandler extends SimpleChannelInboundHandler<RemotingCommand> {
        private final AppContext appContext;

        public NettyServerHandler(AppContext appContext) {
            this.appContext = appContext;
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, RemotingCommand msg) throws Exception {
            // Handle the incoming command
            appContext.getCommandProcessor().processCommand(msg, ctx.channel());
        }
    }

    public class NettyConnectManageHandler extends ChannelDuplexHandler {
        private final ChannelEventListener channelEventListener;

        public NettyConnectManageHandler(ChannelEventListener channelEventListener) {
            this.channelEventListener = channelEventListener;
        }

        @Override
        public void channelRegistered(ChannelHandlerContext ctx) throws Exception {
            LOGGER.info("Channel registered: {}", ctx.channel().remoteAddress());
            if (channelEventListener != null) {
                channelEventListener.onChannelRegistered(ctx.channel());
            }
        }

        @Override
        public void channelUnregistered(ChannelHandlerContext ctx) throws Exception {
            LOGGER.info("Channel unregistered: {}", ctx.channel().remoteAddress());
            if (channelEventListener != null) {
                channelEventListener.onChannelUnregistered(ctx.channel());
            }
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) throws Exception {
            LOGGER.info("Channel active: {}", ctx.channel().remoteAddress());
            if (channelEventListener != null) {
                channelEventListener.onChannelActive(ctx.channel());
            }
        }

        @Override
        public void channelInactive(ChannelHandlerContext ctx) throws Exception {
            LOGGER.info("Channel inactive: {}", ctx.channel().remoteAddress());
            if (channelEventListener != null) {
                channelEventListener.onChannelInactive(ctx.channel());
            }
        }

        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof IdleStateHandler.IdleStateEvent) {
                IdleState state = ((IdleStateHandler.IdleStateEvent) evt).state();
                if (state == IdleState.ALL_IDLE) {
                    LOGGER.warn("Channel idle: {}", ctx.channel().remoteAddress());
                    if (channelEventListener != null) {
                        channelEventListener.onChannelIdle(ctx.channel());
                    }
                    ctx.close();
                }
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) throws Exception {
            LOGGER.error("Exception caught in channel: {}", ctx.channel().remoteAddress(), cause);
            if (channelEventListener != null) {
                channelEventListener.onExceptionCaught(ctx.channel(), cause);
            }
            ctx.close();
        }
    }
}

