import javax.net.ssl.SSLException;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.util.concurrent.atomic.AtomicInteger;

public class HttpUploadServer {
    private static final boolean SSL = Boolean.getBoolean("ssl");
    private static final int PORT = Integer.parseInt(System.getProperty("port", SSL ? "8443" : "8080"));
    private static final String UPLOAD_DIR = "uploads/";

    public static void main(String[] args) throws Exception {
        // Ensure the upload directory exists
        Files.createDirectories(Paths.get(UPLOAD_DIR));

        // Configure SSL context if enabled
        SslContext sslCtx = null;
        if (SSL) {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
        }

        // Create event loop groups
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) {
                     ChannelPipeline pipeline = ch.pipeline();
                     if (sslCtx != null) {
                         pipeline.addLast(sslCtx.newHandler(ch.alloc()));
                     }
                     pipeline.addLast(new HttpRequestDecoder());
                     pipeline.addLast(new HttpResponseEncoder());
                     pipeline.addLast(new HttpObjectAggregator(65536));
                     pipeline.addLast(new FileUploadHandler());
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 128)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            // Bind and start to accept incoming connections.
            ChannelFuture f = b.bind(PORT).sync();

            System.out.println("Server started on port " + PORT + " with SSL: " + SSL);

            // Wait until the server socket is closed.
            f.channel().closeFuture().sync();
        } finally {
            // Shut down all event loops to terminate all threads.
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    private static class FileUploadHandler extends SimpleChannelInboundHandler<FullHttpRequest> {
        private static final AtomicInteger counter = new AtomicInteger(0);

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest request) throws Exception {
            if (request.getMethod() != HttpMethod.POST) {
                sendError(ctx, HttpResponseStatus.METHOD_NOT_ALLOWED);
                return;
            }

            if (!request.headers().contains("Content-Type")) {
                sendError(ctx, HttpResponseStatus.BAD_REQUEST);
                return;
            }

            if (request.content().readableBytes() > 10 * 1024 * 1024) { // 10 MB
                sendError(ctx, HttpResponseStatus.REQUEST_ENTITY_TOO_LARGE);
                return;
            }

            String fileName = "upload-" + counter.incrementAndGet() + ".tmp";
            File file = new File(UPLOAD_DIR, fileName);
            FileOutputStream fos = new FileOutputStream(file);

            fos.write(request.content().array());
            fos.close();

            String response = "File uploaded successfully: " + fileName;
            ByteBuf content = Unpooled.copiedBuffer(response.getBytes());
            FullHttpResponse httpResponse = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK, content);
            httpResponse.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/plain; charset=UTF-8");
            httpResponse.headers().set(HttpHeaderNames.CONTENT_LENGTH, content.readableBytes());

            ctx.writeAndFlush(httpResponse).addListener(ChannelFutureListener.CLOSE);
        }

        private void sendError(ChannelHandlerContext ctx, HttpResponseStatus status) {
            String errorMessage = "Error: " + status.reasonPhrase();
            ByteBuf content = Unpooled.copiedBuffer(errorMessage.getBytes());
            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, status, content);
            response.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/plain; charset=UTF-8");
            response.headers().set(HttpHeaderNames.CONTENT_LENGTH, content.readableBytes());

            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
        }
    }
}

