import java.io.IOException;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Map;
import java.util.stream.Collectors;
import com.google.gson.JsonElement;
import com.google.gson.JsonParser;

public class Network {
    private static final HttpClient httpClient = HttpClient.newHttpClient();

    public HttpResponse<String> makePOSTRequest(String url, Map<Object, Object> parameters, Map<String, String> headers, boolean convertParametersToJSON) throws IOException, InterruptedException {
        String body;
        if (convertParametersToJSON) {
            body = new Gson().toJson(parameters);
        } else {
            body = buildFormDataFromMap(parameters);
        }

        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .headers(headers.entrySet().stream()
                        .flatMap(e -> java.util.Arrays.stream(e.getValue().split(",")))
                        .distinct()
                        .map(k -> k + ": " + headers.get(k))
                        .toArray(String[]::new))
                .POST(HttpRequest.BodyPublishers.ofString(body))
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        if (failure(response.statusCode())) {
            throw new IOException("HTTP error code: " + response.statusCode());
        }
        return response;
    }

    private HttpRequest.BodyPublisher buildFormDataFromMap(Map<Object, Object> data) {
        return HttpRequest.BodyPublishers.ofString(data.entrySet().stream()
                .map(e -> e.getKey() + "=" + e.getValue())
                .collect(Collectors.joining("&")));
    }

    public JsonElement makeJsonRequest(String url) throws IOException, InterruptedException {
        HttpResponse<String> response = httpClient.send(HttpRequest.newBuilder()
                .uri(URI.create(url))
                .GET()
                .build(), HttpResponse.BodyHandlers.ofString());

        if (failure(response.statusCode())) {
            throw new IOException("HTTP error code: " + response.statusCode());
        }

        return JsonParser.parseString(response.body());
    }

    private boolean failure(int statusCode) {
        return statusCode < 200 || statusCode >= 300;
    }
}

