import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.ArrayList;
import java.util.List;

public class FileChannelProcessor {
    private static final int BUFFER_SIZE = 500 * 1024 * 1024; // 500MB
    private final FileChannel channel;
    private List<Buffer> buffers;

    public FileChannelProcessor(Path filePath) throws IOException {
        this.channel = FileChannel.open(filePath, StandardOpenOption.READ);
        this.buffers = new ArrayList<>();
    }

    public List<Buffer> createBuffers() throws IOException {
        long fileSize = channel.size();
        long position = 0;

        while (position < fileSize) {
            long remaining = fileSize - position;
            int size = (int) Math.min(BUFFER_SIZE, remaining);
            ByteBuffer buffer = ByteBuffer.allocate(size);
            channel.read(buffer, position);
            buffer.flip();

            int length = findLastNewline(buffer);
            if (length == -1) {
                length = buffer.limit();
            }

            buffers.add(new Buffer(buffer, length));
            position += length;
        }

        return buffers;
    }

    private int findLastNewline(ByteBuffer buffer) {
        int lastNewline = -1;
        for (int i = 0; i < buffer.limit(); i++) {
            if (buffer.get(i) == '\n') {
                lastNewline = i;
            }
        }
        return lastNewline;
    }

    public void close() throws IOException {
        channel.close();
    }

    public record Buffer(ByteBuffer byteBuffer, int length) {}
}

