            throw new StoreTimeoutException(
                "Put operation timed out while waiting for an ack from Kafka", e);
        } catch (KafkaException ke) {
            throw new StoreException("Put operation to Kafka failed", ke);
        }
    }

    public void close() {
        producer.close();
    }
}

